using NFT1Forge.OSY.DataModel;using NFT1Forge.OSY.JsonModel;using NFT1Forge.OSY.System;using UnityEngine;using UnityEngine.UI;namespace StarterAssets{    public class UICanvasControllerInput : MonoBehaviour    {        [SerializeField] private Image m_ImageSpecialWeaponDisbled;        [SerializeField] private Image m_ImageSpecialWeapon;        [SerializeField] private Transform m_TransformJoyStick;        [SerializeField] private Transform m_TransformSpecialWeapon;        [Header("Output")] public StarterAssetsInputs starterAssetsInputs;        private float m_SpecialWeaponChargeTime;        public void SetUp(float specialWeaponChargeTime)        {#if UNITY_WEBGL            if (BuildType.WebPlayer == SystemConfig.BuildType)            {                if (1 == WebBridgeUtils.GetMobileStatus())                {                    m_TransformJoyStick.SetActive(true);                }            }#elif UNITY_ANDROID && !UNITY_EDITOR            m_TransformJoyStick.SetActive(true);#endif            SpecialWeaponDataModel weapon = InventorySystem.I.GetActiveWeapon();            if (null != weapon)            {                m_TransformSpecialWeapon.SetActive(true);                AssetCacheManager.I.SetSprite(m_ImageSpecialWeaponDisbled, weapon.GetImagePath());                AssetCacheManager.I.SetSprite(m_ImageSpecialWeapon, weapon.GetImagePath());                m_SpecialWeaponChargeTime = specialWeaponChargeTime;            }            else            {                m_TransformSpecialWeapon.SetActive(false);            }        }        public void VirtualMoveInput(Vector2 virtualMoveDirection)        {            starterAssetsInputs.MoveInput(virtualMoveDirection);        }        public void VirtualJumpInput(bool virtualJumpState)        {            starterAssetsInputs.FireInput(virtualJumpState);        }        /// <summary>        /// Update progress ui        /// </summary>        /// <param name="time"></param>        public void OnSpecialWeaponTimeChanged(float time)        {            float progress = time * 100 / m_SpecialWeaponChargeTime;            m_ImageSpecialWeapon.fillAmount = progress * 0.01f;        }        /// <summary>        /// Reset time to zero        /// </summary>        public void OnSpecialFired()        {            m_ImageSpecialWeapon.fillAmount = 0f;        }    }}